class TimestepBlock(nn.Module):
    """
    Any module where forward() takes timestep embeddings as a second argument.
    """

    @abstractmethod
    def forward(self, x, emb):
        """
        Apply the module to `x` given `emb` timestep embeddings.
        """
        
class UpBlocktp(nn.Module):
    
    @abstractmethod
    def forward(self, x, tppad):
        """
        Apply the module to `x` given `tppad` upconv padding.
        """
        

class TimestepEmbedSequential(nn.Sequential, TimestepBlock):
    """
    A sequential module that passes timestep embeddings to the children that
    support it as an extra input.
    """

    def forward(self, x, emb):
        for layer in self:
            if isinstance(layer, TimestepBlock):
                x = layer(x, emb)
            else:
                x = layer(x)
        return x

class TimestepEmbedTPSequential(nn.Sequential, TimestepBlock, UpBlocktp):
    """
    A sequential module that passes timestep embeddings to the children that
    support it as an extra input.
    """

    def forward(self, x, emb, tppad):
        for layer in self:
            if isinstance(layer, TimestepBlock):
                x = layer(x, emb)
            elif isinstance(layer, UpBlocktp):
                x = layer(x, tppad)
            else:
                x = layer(x)
        return x

    
class SSM_block(nn.Sequential):
    def __init__(self, dims=512):
        super().__init__()
        self.to_ssm = Rearrange('t c h w -> 1 (h w t) c')
#         self.out_ssm = Rearrange('(h w) t c -> t c h w', h = 4)
        self.SSModel = Mamba(    
            d_model=dims, # Model dimension d_model
            d_state=16,  # SSM state expansion factor
            d_conv=4,    # Local convolution width
            expand=2, )
    
    def forward(self, x):
        t,c,h1,w = x.shape
        x = self.to_ssm(x)
#         print(x.shape,'shape1')
        x = self.SSModel.forward(x)
#         print(x.shape,'shape2')
        x = Rearrange('1 (h w t) c -> t c h w', h = h1, t = 12)(x)
#         print(x.shape,'shape3')
        return x.float()

